// *****************************************************// * This example is about Counter by using class      *// * Created by Sevki Bekir                            *// * Date: April 29, 2015                              *// *************************************************************************//// 1.	Write a program to convert a Roman number into its decimal equivalent.// For this purpose create class romanType that will store the roman number// as a character array of 5 elements. Class will have roman (char[]) and// decimal (int) data members.// -constructor  will input roman number character by character and store it into the array// -member function that converts roman number into decimal and sets the decimal data member// -member function that displays the roman and its decimal equivalent on the screen.// Test your program by creating an object of this class and find and display// their decimal equivalent for numbers  MCXIV, CCLIX, MDCLX.// **************************************************************************#include <iostream>#define SIZE 5using namespace std;class romanType{  char romanNumber[SIZE+1];  int decimal;  public:  void convertFunction()  {    int i;       for(int i=0;i<5;i++)        if (romanNumber[i]=='M')          decimal+=1000;        else if(romanNumber[i]=='D')          decimal+=500;        else if(romanNumber[i]=='C')            if ((romanNumber[i+1]=='D' || romanNumber[i+1]=='M')&&i<=4)                decimal-=100;            else                decimal+=100;        else if(romanNumber[i]=='L')          decimal+=50;        else if(romanNumber[i]=='X')            if ((romanNumber[i+1]=='L' || romanNumber[i+1]=='C')&&i<=4)                decimal-=10;            else                decimal+=10;        else if(romanNumber[i]=='V')          decimal+=5;        else if(romanNumber[i]=='I')            if ((romanNumber[i+1]=='X' || romanNumber[i+1]=='V')&&i<=4)                decimal-=1;            else                decimal+=1;        else          cout << "Wrong";    }    romanType(){      decimal=0;      cout<<"Write a five-digit roman number:";      cin>>romanNumber;    }    void print()    {      cout << "The convertion is successfully"<<endl;      cout << "The convertion is "<< decimal<<endl;    }};int main(){  romanType r1;  r1.convertFunction();  r1.print();  return 0;}